import React from 'react';
import { Modal, Button } from 'react-bootstrap';
import Footer from '../../components/Footer';
import Header from '../../components/Header';
import Input from '../../components/Input';
import ButtonInscricao from '../../components/Button-inscricao';
import ButtonVermais from '../../components/Button-ver-mais';
import ImgLupa from '../../assets/image/icone-busca.png';
import '../../assets/style/global.css';
import './style.css';

function Vagas() {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <div>
            <Header />
            <div className="vagas">
                <div className="title">
                    <h1>Encontre a sua vaga!</h1>
                </div>
                <div className="pesquisa">
                    <img src={ImgLupa} alt="Icone de uma lupa" />

                    {/* Mecanismo de pesquisa de vagas */}
                    <Input id="InputEdit" name="pesquisa" label="" placeholder="Buscar vaga" />
                </div>
                <div className="Conteudo">
                    <div className="TitleImg">
                        <div className="img">
                            {/* Imagem da vaga */}
                            <img src="" alt="Foto vaga" />
                        </div>
                        <div className="title1">
                            {/* Nome da vaga */}
                            <h1>Analista de sistemas</h1>

                            {/* Local da vaga */}
                            <h2>São Paulo, Zona Leste</h2>

                            {/* Salário */}
                            <h3>R$2000.00</h3>
                        </div>
                    </div>
                    <div className="buttons">
                        <div className="inscricao">
                            {/* Botão para inscrever-se na vaga */}
                            <ButtonInscricao value="Inscrever-se" />
                        </div>
                        <div className="verMais">
                            {/* Botão para ver mais informação sobre a vaga */}
                            <ButtonVermais id="btn1" value="Ver mais..." />
                        </div>
                    </div>
                </div>
            </div>
            <Footer />

            <Modal className="Modal" show={show} onHide={handleClose}>
                <Modal.Header id="ModalColor" closeButton>
                    <Modal.Title id="ModalColor1" >Alteração de dados</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Input id="InputEdit1" name="Alteração" label="Insira o dado atualizado: " />
                </Modal.Body>
                <Modal.Footer id="ModalColor">
                    <Button id="btn" variant="primary" onClick={handleClose}>
                        Salvar
            </Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
}

export default Vagas;
