import React, { useEffect, useState } from 'react';
import { Modal, Button } from 'react-bootstrap';
import Footer from '../../components/Footer';
import Header from '../../components/Header';
import Input from '../../components/Input';
import ButtonInscricao from '../../components/Button-inscricao';
import ImgLupa from '../../assets/image/icone-busca.png';
import '../../assets/style/global.css';
import './style.css';


function Vagas() {

    // Variáveis para o Modal funcionar
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);

    const handleShow = (id: any) => {
        const item = vagas.filter((element: any) => element.id === id);
        setModal(item);
        setShow(true);
    }

    //Começo funcionalidades vagas
    const [vaga, setVaga] = useState(''); //duas '' pois é uma string
    const [idVaga, setIdVaga] = useState(0); //numero 0 pois é um int Id

    const [empresas, setEmpresas] = useState([]);
    const [vagas, setVagas] = useState([]);
    const [modal, setModal] = useState([]);

    useEffect(() => {
        listar();
    }, []);

    //Listar vagas
    const UrlVagas = 'http://localhost:5000/api/Vagas';

    const listar = () => {

        fetch(UrlVagas, {
            method: 'GET'
        })
            .then(response => response.json())
            .then(dados => {
                setVagas(dados)
                console.log(dados);
            })

            .catch(Erro => console.error(Erro));
    }


    //Pesquisa de vaga
    var InputName = document.getElementById('InputEdit');
    const Buscando = vagas.map((e: any) => e.cargo == InputName);
    function Buscar() {
        if (Buscando != null) {
            return Buscando;
        }
        else {
            return 'Erro';
        }
    }


    return (
        <div>
            <Header />
            <div className="vagas">
                <div className="title">
                    <h1>Encontre a sua vaga!</h1>
                </div>
                <div className="pesquisa">
                    <img src={ImgLupa} alt="Icone de uma lupa" />
                    {/* Mecanismo de pesquisa de vagas */}
                    <input type="text" id="InputEdit" name="pesquisa" label="" placeholder="Buscar vaga" />
                    <button className="BtnPesquisa" onClick={Buscar}>Buscar</button>
                </div>
                {
                    vagas.map((item: any) => {
                        return (
                            <div className="fetch" key={item.idVagas}>
                                <div className="Conteudo" >
                                    <div className="TitleImg">
                                        <div className="img">
                                            {/* Imagem da vaga */}
                                            <img src="" alt="Foto vaga" />
                                        </div>
                                        <div className="title1">
                                            {/* Nome da vaga */}
                                            <h1>{item.cargo}</h1>

                                            {/* Local da vaga */}
                                            <h2>{item.localVaga}</h2>

                                            {/* Salário */}
                                            <h3>{'Salário: R$ ' + item.salario}</h3>
                                        </div>


                                    </div>

                                    <div className="buttons">
                                        <div className="inscricao">
                                            {/* Botão para inscrever-se na vaga */}
                                            <ButtonInscricao value="Inscrever-se" />
                                        </div>
                                        <div className="verMais">
                                            {/* Botão para ver mais informação sobre a vaga */}
                                            <button onClick={() => handleShow(item.id)} id="btn1" value="VerMais">Ver mais...</button>
                                        </div>
                                    </div>
                                </div>
                            </div>

                        )
                    })
                }
            </div>
            <Footer />
            <div>
                {
                    modal.map((dado: any) => {
                        return (
                            <Modal className="Modal" show={show} onHide={handleClose} >
                                <Modal.Header id="ModalColor" closeButton>
                                    <Modal.Title id="ModalColor1" >{dado.idEmpresa.Nome}</Modal.Title>
                                </Modal.Header>
                                <Modal.Body className="ModalBody">
                                    <div className="bodyModal">
                                        <div className="img">
                                            {/* Imagem da vaga */}
                                            <img src="" alt="Foto vaga" />
                                        </div>
                                        <div className="title1">
                                            {/* Nome da vaga */}
                                            <h1>{dado.cargo}</h1>

                                            {/* Local da vaga */}
                                            <h2>{dado.localVaga}</h2>

                                            {/* Salário */}
                                            <h3>{'Salário: R$ ' + dado.salario}</h3>
                                        </div>
                                    </div>

                                    <div className="informacoes">
                                        <div className="ulInformacoes">
                                            <h5>Informações</h5>
                                            <ul>
                                                <li>{'Recrutador: ' + dado.nomeRecrutador}</li>
                                                <li id="liInformacoesMeio">{'Quantidade de vagas: ' + dado.qntVagas}</li>
                                                <li>{'Tipo de contrato: ' + dado.tipoContrato}</li>
                                            </ul>
                                        </div>
                                        <p>{dado.descricao}</p>
                                    </div>

                                </Modal.Body>
                                <Modal.Footer id="ModalColor">
                                    <Button id="btn" variant="primary" onClick={handleClose}>Inscrever-se</Button>
                                </Modal.Footer>
                            </Modal >
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Vagas;
