import React, { useState} from 'react';
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import '../../assets/style/global.css';
import Input from '../../components/Input';
import imgcadastro from '../../assets/image/imgcadastro.png';
import './style.css';
import Button  from "../../components/Button";
import { useHistory } from 'react-router-dom';



function Cadastro() {

    const [usuario, setUsuario] = useState('');
    const [DataNascimento, setDataNascimento] = useState('');
    const [cpf, setCpf] = useState('');
    const [sexo, setSexo] = useState('');
    const [matricula, setMatricula] = useState('');
    const [escola, setEscola] = useState('');
    const [email, setEmail] = useState('');
    const [senha, setSenha] = useState('');
    const [telefone, setTelefone] = useState('')
    const [EstadoCivil, setEstadoCivil] = useState('');
    const [nivel, setNivel] = useState('');
    const [TipoCurso, setTipoCurso] = useState('');
    const [curso, setCurso] = useState('');
    const [turma, setTurma] = useState('');
    const [turno, setTurno] = useState('');
    const [termo, setTermo] = useState('');


    var history = useHistory();

    const salvar = () => {

        const Tbdados ={
            cpf : cpf,
            NumMatricula: matricula,
            senha: senha
        }

        const urlDados = 'http://localhost:5000/api/Dados/';
        var idUsuario = 0;
        const urlRequest ='http://localhost:5000/api/Usuarios/';

        fetch(urlDados , {
            method: "POST",
            body: JSON.stringify(Tbdados),
            headers: {
                'Content-Type': 'application/json',
            }
        })
        .then(Response => Response.json())
        .then(dados => {
            idUsuario = dados;
        })
        .then(() =>{

            const form = {
                nome: usuario,
                dataNascimento: DataNascimento,
                sexo: sexo,
                escola: escola,
                email: email,
                telefone: telefone,
                estadoCivil: EstadoCivil,
                nivel: nivel,
                tipoCurso: TipoCurso,
                curso: curso,
                turma: turma,
                turno: turno,
                termo: parseInt(termo),
                idTipoUsuario: 3,
                idDados: idUsuario
            };

            console.log(form);
                fetch(urlRequest , {
                method: "POST",
                body: JSON.stringify(form),
                headers: {
                    'Content-Type': 'application/json',
                }
            }).then(()=>{
                history.push('/Login');
                console.log("UsuÃ¡rio cadastrado");
            })
        })
        .catch(err => console.error(err));
    }

    return (
        
        <div className="imgcadastro">
            <Header />
            
            <div className="cadastro-centralizado">

            
           
            <div className="centralizado">
            
                
            <div className="formulario">          
                
                <form onSubmit={event => {
                    event.preventDefault();
                    salvar();
                }}>

                <div id="titulo">
                  <h3>Cadastro</h3>
                </div>
                
                <div className="informaÃ§ao">
                    <Input  type="text" name="nome" label="Nome completo:" 
                    onChange={e => setUsuario(e.target.value)} />
                </div>

                <div className="informaÃ§ao">
                    <Input type="date" name="DataNascimento" label="Data de Nascimento:" 
                    onChange={e => setDataNascimento(e.target.value)}/>
                </div>

                
                <div className="genero">
            
                Sexo:
                
                <label className="masculino">
                    
                    <input  type="radio" value="Masculino"  name="genero"
                      onChange={e => setSexo(e.target.value)} />
                          Masculino
                </label>

               
                <label className="feminino" >
                    <input  type="radio" value="Feminino"  name="genero"
                    onChange={e => setSexo(e.target.value)}/>
                      Feminino
                </label>
                           
                </div>

               <div className='sexo'>

               <label>
                <input  type="radio" value="Prefiro nÃ£o me indentificar"  name="genero"
                    onChange={e => setSexo(e.target.value)}/>
                      Prefiro nÃ£o me indentificar
                </label>

               </div>
                
                <div className="estadoCivil">
                    <label>
                        Estado civil:
                        <select required
                            onChange={e => setEstadoCivil(e.target.value)}>
                            <option value="" selected disabled hidden>Selecione seu Estado Civil</option>
                            <option  value='solteiro'>Solteiro(a)</option>
                            <option  value='casado'>Casado(a)</option>
                            <option  value='divorciado'>Divorciado(a)</option>
                            <option  value='viuvo'>ViÃºvo(a)</option>
                            <option  value='separado'>Separado(a)</option>
                            <option  value='outro'>Outro</option>
                        </select>
                    </label>
                </div>

                <div className="informaÃ§ao">
                    <Input type="text" name="CPF" label="CPF:"
                    onChange={e => setCpf(e.target.value)}/>
                </div>

                <div className="informaÃ§ao">
                    <Input type="text" name="Telefone" label="Telefone:" 
                    onChange={e => setTelefone(e.target.value)} />
                </div>

                <div className="informaÃ§ao">
                    <Input type="text" name="RM" label="RM:" 
                    onChange={e => setMatricula(e.target.value)}/>
                </div>

