import React from 'react';
import './style.css';
import { parseJWT } from '../../services/auth';
import '../../assets/style/global.css';
import { Nav, Image, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link, useHistory } from 'react-router-dom';
import Logo from '../../assets/image/Logo.png';
import Button from '../Button';

const Header = () => {

    let history = useHistory();

    const sair = () => {
        localStorage.removeItem('Real-Vagas-Token');
        history.push('/');
    }

    const menu = () => {
        const token = localStorage.getItem('Real-Vagas-Token');
        console.log("==========")
        console.log(parseJWT())
        console.log("==========")

        if (token === undefined || token === null) {
            return (
                <div className="Container-Nav">
                    <nav>
                        <ul>
                            <li><img src={Logo} alt="Logo"/></li>
                        </ul>
                        <ul>
                            <li><Link to="/">Home</Link></li>
                            <li><Link to="/Vagas">Vagas</Link></li>
                            <li><Link to="/Dicas">Dicas do Possarle</Link></li>
                            <li><Link to ="/Sobre">Sobre Nós</Link></li>
                        </ul>
                        <ul>
                            <li><Link to="/Login">Login</Link></li>
                            <li><Link to="/Cadastro"><Button id="btnCadastro" name="Cadastre-se"></Button></Link></li>
                        </ul>
                    </nav>
                </div>
            )
        } else {
            if (parseJWT() === 1) {
                return (
                    <div className="Container-Nav">
                    <nav>
                        <ul>
                            <li><img src={Logo} alt="Logo"/></li>
                        </ul>
                        <ul>
                            <li><Link to="/">Home</Link></li>
                            <li><Link to="/Vagas">Vagas</Link></li>
                            <li><Link to="/Dicas">Dicas do Possarle</Link></li>
                            <li><Link to ="/Sobre">Sobre Nós</Link></li>
                        </ul>
                        <ul>
                            <li><Link to="/Login"to="" onClick={event => {
                                event.preventDefault();
                                sair();
                            }}>Sair</Link></li>
                            <li><Link to="/Cadastro"><Button id="btnCadastro" name="Cadastre-se"></Button></Link></li>
                        </ul>
                    </nav>
                </div>
                )
            } else if (parseJWT() === 2) {
                return (
                    <div className="d-flex">
                        <Nav.Item className="nav-person">
                            <Nav.Link ><Link to="/Dashboard">Perfil</Link></Nav.Link>
                        </Nav.Item>

                        <Nav.Item className="person-nav">
                            <Nav.Link><Link to="" onClick={event => {
                                event.preventDefault();
                                sair();
                            }}>Sair</Link></Nav.Link>
                        </Nav.Item>
                    </div>
                )
            } else if (parseJWT() === 3 || parseJWT() === 4) {
                return (
                    <div className="d-flex">
                        <Nav.Item className="nav-person">
                            <Nav.Link ><Link to="/Perfil">Perfil</Link></Nav.Link>
                        </Nav.Item>

                        <Nav.Item className="person-nav">
                            <Nav.Link><Link to="" onClick={event => {
                                event.preventDefault();
                                sair();
                            }}>Sair</Link></Nav.Link>
                        </Nav.Item>
                    </div>
                )
            }
        }
    }

    return (
        <div>
                {menu()}
        </div>
    )
}

export default Header;
